int MainWindow::matchInteraction()
{
    int information = 0;
    information = map[block][player_x][player_y];

    switch(information){
    case 0:break;
    case 1:
        player_x -= 19;
        ui->label->move(player_x*UNITLENGTH,player_y*UNITLENGTH);
        hideBlockLabels(block);
        block += 1;
        break;
//        Interaction.setBlockLabels(ui);
    case 2:
        player_y -= 11;
        ui->label->move(player_x*UNITLENGTH,player_y*UNITLENGTH);
        hideBlockLabels(block);
        block += 4;
        break;
    case 3:
        player_x += 19;
        ui->label->move(player_x*UNITLENGTH,player_y*UNITLENGTH);
        hideBlockLabels(block);
        block -= 1;
        break;
    case 4:
        player_y += 11;
        ui->label->move(player_x*UNITLENGTH,player_y*UNITLENGTH);
        hideBlockLabels(block);
        block -= 4;
        break;
    }
    return information;
}

